        <MidSection  upload={this.handleUpload} load={this.handleselectedFile}>
        <FileUpload upload={this.handleUpload} name={this.state.selectedFileName} load={this.handleselectedFile}/>
        </MidSection>


      </div>
    );
  }
}

export default App;
