import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import FileUpload from './Components/FileUpload';
import axios from 'axios';

const endpoint = 'http://localhost:4000/images'
var answer;

class App extends Component {
  constructor() {
    super()
    this.state = {
      selectedFile: null,
      loaded: 0,
      respond: false
    }
  }
  handleselectedFile = event => {
    this.setState({
      selectedFile: event.target.files[0],
      loaded: 0,
    })
  }

  handleUpload = () => {
    const data = new FormData()
    data.append('file', this.state.selectedFile, this.state.selectedFile.name)
    console.log("DAta", this.state.selectedFile);

    axios
      .post(endpoint, data, {
        onUploadProgress: ProgressEvent => {
          this.setState({
           loaded: (ProgressEvent.loaded / ProgressEvent.total) * 100,
          })
        },
      })
      .then(res => {
        this.setState({
          respond: true
        })
        answer = res.data.response[0].name;
        console.log(",,,,,,,,,,,,,",answer);
      });
  }
  render() {
    return (
      <div className="App">
        <header className="App-header">
          <FileUpload upload={this.handleUpload} load={this.handleselectedFile}/>
          <div> {Math.round(this.state.loaded, 2)} %</div>
        </header>
        {
            this.state.respond && <div>HH{answer}</div>
        }
      </div>
    );
  }
}

export default App;
